frappe.ui.form.on('Sales Order', {
    refresh: function(frm) {
        var hideButton = function() {
            cur_frm.fields_dict['items'].grid.wrapper.find('.btn-open-row').hide();
        };

        hideButton();

        $(document).on('grid-row-render', function(event) {
            hideButton();
        });
    }
});

frappe.ui.form.on('Sales Order', {
    currency: function(frm) {
        // Fetch the warehouse associated with the selected currency
        if(frm.doc.currency) {
            frappe.call({
                method: 'frappe.client.get_list',
                args: {
                    doctype: 'Warehouse',
                    filters: {
                        'custom_currency': frm.doc.currency
                    },
                    fields: ['name']
                },
                callback: function(response) {
                    if(response && response.message && response.message.length > 0) {
                        let warehouse = response.message[0].name;
                        // Set the fetched warehouse in the appropriate field
                        frm.set_value('warehouse', warehouse);
                    } else {
                        // Clear the warehouse field if no match is found
                        frm.set_value('warehouse', '');
                        frappe.msgprint(__('No warehouse found for the selected currency.'));
                    }
                }
            });
        } else {
            // Clear the warehouse field if currency is not set
            frm.set_value('warehouse', '');
        }
    }
});

