frappe.ui.form.on('Sales Order', {
    onload: function(frm) {
        frm.set_query('set_warehouse', function() {
            if (frm.doc.currency) {
                return {
                    filters: {
                        'custom_currency': frm.doc.currency
                    }
                };
            } else {
                return {
                    filters: {}
                };
            }
        });
    },
    currency: function(frm) {
        frm.set_value('set_warehouse', '');
    }
});

frappe.ui.form.on('Sales Invoice', {
    refresh: function(frm) {
        // Add any necessary code to run when the form is refreshed
    },
    custom_job_number: function(frm) {
        // Fetch customer and custom_job_number from the form
        let customer = frm.doc.customer;
        let custom_job_number = frm.doc.custom_job_number;

        if (customer && custom_job_number) {
            // Call server-side method to fetch data using frappe.client.get
            frappe.call({
                method: "frappe.client.get",
                args: {
                    doctype: "Job Details",
                    name: custom_job_number
                },
                callback: function(r) {
                    if (r.message) {
                        // Find the specific row in the child table that matches the customer
                        let found = false;
                        for (let row of r.message.console_shipment) {
                            if (row.customer == customer) {
                                frm.set_value('custom_mode_of_delivery', row.mode_of_delivery);
                                frm.set_value('custom_cn', row.cn);
                                frm.set_value('custom_payment_type', row.payment_type);
                                found = true;
                                break;
                            }
                        }
                        if (!found) {
                            frappe.msgprint(__('No matching customer found in the Job Details child table.'));
                        }
                    }
                }
            });
        }
    }
});
