frappe.listview_settings['Sales Order'] = {
    onload: function(listview) {
        var df = {
            fieldname: 'sales_person', 
            label: "Sales Person",
            fieldtype: "Link", 
            options: "Sales Person",
            onchange: function() {
                listview.start = 0;
                listview.refresh();
                listview.on_filter_change();
            }
        };
        listview.page.add_field(df);
    }
};

